import {useState} from "react";
import {Button, FormControl, Grid, makeStyles, TextField} from "@material-ui/core";

const styles = makeStyles({
    textfield1: {
        width: "50%",
        position: "sticky",
        padding: "0.7rem",
    },

    textfield2: {
        width: "200px",
        position: "sticky",
        padding: "0.7rem",
    },

    form: {
        position: "sticky",
    },

    button: {
        padding: 15,
    }
});

const CreateUnit = (setAllUnits) => {
    const [unit, setUnit] = useState({
        unit:"",
        topic: "",
        grade: 0,
    })

    const classes = styles();

    return(
        <FormControl className={classes.form}>
            <Grid container spacing={2}>
                <Grid item>
                    <TextField placeholder={"Enter unit title"} required variant={"outlined"} />
                </Grid>
                <Grid item>
                    <TextField placeholder={"Enter unit topic"} variant={"outlined"} />
                </Grid>
                <Grid item>
                    <TextField placeholder={"Enter unit score"} variant={"outlined"} />
                </Grid>
                <Grid item>
                    <Button color={"secondary"} type={"submit"} className={classes.button} disableElevation variant={"contained"}>Add Reminder</Button>
                </Grid>
            </Grid>
        </FormControl>
    )
}

export default CreateUnit;
