import React, { useState } from "react";
import {
  TextField,
  Typography,
  makeStyles,
  Button,
  FormControl,
} from "@material-ui/core";


const reminderStyle = makeStyles({
  textfield1: {
    marginTop: "5px",
    width: "100%",
  },

  textfield2: {
    paddingTop: "100px",
    width: "40%%"
  },

  button: {
    background: "primary",
    marginTop: "4px",
    position: 'absolute', 
    left: '50%', 
    top: '50%', 
    transform: 'translate(30%, -320%)'
  },

  form:{
    position: 'absolute', 
    left: '50%', 
    top: '50%', 
    transform: 'translate(-15%, -300%)'
  }
});


function CreateReminder() {
  const classes = reminderStyle();

  const [reminders, setReminders] = useState({
    title: null,
    due: null,
    completed: false,
  });


  return (
    <div>
      <Typography variant="h5">Create a reminder</Typography>

      <FormControl className={classes.form} noValidate>
        <TextField
          label="Reminder"
          id="margin-none"
          variant="outlined"
          className={classes.textfield1}
          multiline
          required
        />

        <TextField
          type="date"

          className={classes.textfield2}
          variant="outlined"
          required
        />

      </FormControl>
      <Button
        type="submit"
        disableElevation
        variant="contained"
        className={classes.button}
        color="secondary"
      >
        Add Reminder
      </Button>

    </div>
  );
}

export default CreateReminder;
