import React from 'react';
import { TextField, Container, Typography, makeStyles, Button } from '@material-ui/core';

const reminderStyle = makeStyles({
    text:{
        color: "secondary",
        top: "20%"
    },

    textfield1: {
        marginTop: "5px",
        width: "40%"

    },

    textfield2: {
        marginTop: "5px",
        paddingLeft: "0px"

    },

    button: {
        background: "primary",
        marginTop: "4px",
    }
})

function CreateReminder() {
    const classes = reminderStyle();

    return (
        <Container>
            <Typography variant="h5" >
                Creat a reminder
            </Typography>

            <form 
            noValidate 
            >
                <TextField 
                label="Reminder"
                variant="outlined"
                className={classes.textfield1}
                required
                />

                <TextField 
                className={classes.textfield2}
                label="Due Date"
                variant="outlined"
                required
                />
            </form>

            <Button 
            type="submit" 
            disableElevation 
            variant="contained"
            className={classes.button}
            color="secondary"
            >
                Add Reminder
            </Button>
        </Container>
    )
}

export default CreateReminder
