import React from 'react';
import { TextField, Container, Typography, makeStyles, Button } from '@material-ui/core';

const reminderStyle = makeStyles({
    text:{
        color: "secondary"
    },

    textfield: {
        marginTop: "10px",
        background: "secondary"

    },

    button: {
        background: "primary",
        marginTop: "4px"
    }
})

function CreateReminder() {
    const classes = reminderStyle();

    return (
        <Container>
            <Typography 
            variant="h4" 
            align="center"
            className={classes.text}
            >
                Reminders
            </Typography>
            <Typography variant="h6" >
                Creat a reminder
            </Typography>

            <form 
            noValidate 
            >
                <TextField 
                label="Reminder"
                variant="outlined"
                color="secondary"
                className={classes.textfield}
                required
                />

                <TextField 
                className={classes.textfield}
                label="Due Date"
                variant="outlined"
                color="secondary"
                required
                />
            </form>

            <Button 
            type="submit" 
            disableElevation 
            variant="contained"
            className={classes.button}
            >
                Add Reminder
            </Button>
        </Container>
    )
}

export default CreateReminder
