import React, { useState } from "react";
import {
  TextField,

  makeStyles,
  FormGroup,
  Checkbox,
  Typography,
} from "@material-ui/core";
import ReminderButton from "./ReminderButton";

const reminderStyle = makeStyles({
  textfield1: {
    width: "250px",
    position: "absolute",
    left: "50%",
    top: "50%",
    transform: "translate(-100%, -305%)",
  },

  textfield2: {
    width: "200px",
    position: "absolute",
    left: "50%",
    top: "50%",
    transform: "translate(10%, -300%)",
  },

  form: {
    position: "absolute",
    left: "50%",
    top: "50%",
    transform: "translate(-15%, -300%)",
  },
});

function CreateReminder() {
  const classes = reminderStyle();

  const [allReminders, setAllReminders] = useState([]);
  const [reminders, setReminders] = useState({
    title: null,
    due: null,
    completed: false,
  });

  let count = allReminders.length;

  const addSingleReminder = (reminders) => {
    setAllReminders([...allReminders, reminders]);
  };

  const onChecked = (id) => {
    const newReminders = allReminders;

    let toChange = newReminders[id];

    toChange.completed = !toChange.completed;

    newReminders[id] = toChange;

    setAllReminders([...newReminders]);
  };

  return (
    <div>
      <FormGroup
        className={classes.form}
        noValidate>

        <TextField
          label="Reminder"
          id="margin-none"
          variant="outlined"
          className={classes.textfield1}
          multiline
          required
          onChange={e => setReminders({
            ...reminders,
            title: e.target.value
          })}
        />

        <TextField
          type="date"
          className={classes.textfield2}
          variant="outlined"
          required
          onChange={e => setReminders({
            ...reminders,
            message: e.target.value
          })}
        />

        <ReminderButton         
        onSubmit={(e) => {
          e.preventDefault();
          addSingleReminder({
            ...reminders,
            id: count++,
          });
        }}
      />

      </FormGroup>

      {
        allReminders.length > 0 ? allReminders.map(reminders => 
          <div key={reminders.id}>
            <Checkbox
            id={reminders.id}
            checked={reminders.completed}
            name={reminders.id}
            onChange={() => onChecked(reminders.id)}
            />
          </div>)

          :

          <Typography>
            No Reminders Yet
          </Typography>

      }

    </div>
  );
}

export default CreateReminder;
