import React from "react";
import {
  makeStyles,
  Card,
  CardActions,
  CardContent,
  Typography,
  CardActionArea,
} from "@material-ui/core";

const styles = makeStyles({
  root: {
    width: "350px",
    height: "200px",
    display: "flex",
  },

  card: {
    position: "absolute",
    left: "50%",
    top: "50%",
    transform: "translate(-50%, -50%)",
    marginTop: "50px",
    marginBottom: "50px",
  },
});

const styles2 = makeStyles((theme) => ({
  toolbar: theme.mixins.toolbar,

  content: {
    flexGrow: "1",
    backgroundColor: "theme.palette.background.default",
    padding: "theme.spacing(3)",
  },
}));

function UnitCard() {
  const UnitList = [
    {
      unit: "Math",
      topic: "calculus",
      grade: 60,
    },

    {
      unit: "Chem",
      topic: "inorganic chem",
      grade: 60,
    },

    {
      unit: "Bio",
      topic: "cells",
      grade: 60,
    },

    {
      unit: "Physics",
      topic: "inertia",
      grade: 60,
    },
  ];

  const classes = styles();

  const classes2 = styles2();

  return (
    <div className={classes.card}>
      {UnitList.map((val) => (
        <main className={classes2.content}>
            <div className={classes2.toolbar}/>
            <Card className={classes.root}>
                <CardActionArea>
                <CardContent align="center">
                    <Typography variant="h5">{val.unit}</Typography>
                    <Typography variant="h6" color="secondary">
                    {val.grade}%
                    </Typography>
                    <Typography variant="h6">{val.topic}</Typography>
                </CardContent>
                </CardActionArea>
            </Card>
        </main>
      ))}
    </div>
  );
}

export default UnitCard;
