import React, {useState} from 'react';
import { TextField, Typography, makeStyles, Button, Container } from '@material-ui/core';

const reminderStyle = makeStyles({
    textfield1: {
        marginTop: "5px",
        width: "40%"

    },

    textfield2: {
        marginTop: "5px",

    },

    button: {
        background: "primary",
        marginTop: "4px",
    }
});

const [allReminder, setAllReminder] = useState([])

const [reminder, setReminder] = useState({
    title: null,
    due: null,
    completed: false,
})

let count = allReminder.length;

const addReminder = (reminder) => {
    setAllReminder([...allReminder,reminder])
  }

const handleSubmit = (e) => {
    e.preventDefault();
    addReminder ({
        ...reminder,
        id: count++
    })
}

const handleChangeReminder = (e) => {
    setReminder({
        ...reminder,
        title: e.target.value
    })
}

const handleChangeDate = (e) => {
    setReminder({
        ...reminder,
        due: e.target.value
    })
}


function CreateReminder() {
    const classes = reminderStyle();

    return (
        <Container>
            <Typography variant="h5">
                Creat a reminder
            </Typography>

            <form 
            noValidate 
            >
                <TextField 
                label="Reminder"
                variant="outlined"
                className={classes.textfield1}
                required
                onChange = {handleChangeReminder}
                />

                <TextField 
                className={classes.textfield2}
                label="Due"
                variant="outlined"
                required
                onChange={handleChangeDate}
                />
            </form>

            <Button 
            onSubmit={handleSubmit} 
            type="submit" 
            disableElevation 
            variant="contained"
            className={classes.button}
            color="secondary"
            >
                Add Reminder
            </Button>
        </Container>
    )
}

export default CreateReminder
