import React, { useState } from "react";
import {
  TextField,
  Typography,
  makeStyles,
  Button,
  Container,
  Checkbox,
} from "@material-ui/core";
import { CheckBox } from "@material-ui/icons";

const reminderStyle = makeStyles({
  textfield1: {
    marginTop: "5px",
    width: "40%",
  },

  textfield2: {
    marginTop: "5px",
  },

  button: {
    background: "primary",
    marginTop: "4px",
  },
});


function CreateReminder() {
  const classes = reminderStyle();

  const [reminders, setReminders] = useState({
    title: null,
    due: null,
    completed: false,
  });


  return (
    <Container>
      <Typography variant="h5">Creat a reminder</Typography>

      <form noValidate>
        <TextField
          label="Reminder"
          variant="outlined"
          className={classes.textfield1}
          required
        />

        <TextField
          type="date"
          className={classes.textfield2}
          variant="outlined"
          required
        />

      </form>

      <Button
        type="submit"
        disableElevation
        variant="contained"
        className={classes.button}
        color="secondary"
      >
        Add Reminder
      </Button>

      <ul>
        {reminders.map((reminder, val) => (
          <CheckBox value={reminder.title}/>
        ))}
      </ul>
    </Container>
  );
}

export default CreateReminder;
